mapper.select:
SELECT * FROM daangn_member
( username LIKE CONCAT('%', #{search}, '%') OR name LIKE CONCAT('%', #{search}, '%') OR nickName LIKE CONCAT('%', #{search}, '%') )
ORDER BY idx DESC LIMIT #{startNo}, #{sizeOfPage}

mapper.update:
update daangn_member

    role = #{role}
    password = #{password}
    nickName = #{nickName}
    email = #{email}
    emailOk = #{emailOk}
    stAddress = #{stAddress},
    dtAddress = #{dtAddress}

where idx = #{idx}
