mapper.select:
SELECT dlb.*, dlc.categoryName AS categoryName, dm.nickName AS nickName, duf.saveFileName as userProfile FROM daangn_life_board dlb JOIN daangn_member dm ON dlb.userRef = dm.idx LEFT JOIN daangn_user_file duf on dlb.userRef = duf.ref JOIN daangn_life_category dlc ON dlb.categoryRef = dlc.idx WHERE
dlb.idx < #{lastItemIdx}
AND dlb.categoryRef = #{categoryRef}
AND dlb.loc1 = #{region}
AND dlb.loc2 = #{gu}
AND dlb.loc3 = #{dong}
AND dlb.title LIKE CONCAT('%', #{search}, '%')
AND dlb.userRef = #{userRef}
ORDER BY dlb.idx DESC LIMIT #{sizeOfPage}

mapper.update:
UPDATE daangn_life_board
SET readCount = readCount + 1
WHERE idx = #{idx}

UPDATE daangn_life_board
SET likeCount = likeCount + 1
WHERE idx = #{idx}

UPDATE daangn_life_board
SET likeCount = likeCount - 1
WHERE idx = #{idx}

UPDATE daangn_life_board
SET commentCount = commentCount + 1
WHERE idx = #{idx}

UPDATE daangn_life_board
SET commentCount = commentCount - #{commentCount}
WHERE idx = #{idx}
