mapper.select:
SELECT dub.*, duc.categoryName AS categoryName, dus.statusName AS statusName FROM daangn_usermarket_board dub JOIN ( SELECT dubr.idx AS idx, dubr.boardRef FROM daangn_usedmarket_board_reserve dubr WHERE dubr.userRef = #{userRef} AND dubr.interaction = 1 ) dubr ON dubr.boardRef = dub.idx JOIN daangn_usedmarket_category duc ON dub.categoryRef = duc.categoryIdx JOIN daangn_usedmarket_status dus ON dub.statusRef = dus.statusIdx WHERE
dub.idx < #{lastItemIdx}
ORDER BY dubr.idx desc LIMIT #{sizeOfPage}
